attrs.script:
echo "Hi,

PFA runbook report.

Thanks & Regards,
Nucalm-Devops" > /tmp/test2.txt

echo "<html><head><style>
table {
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;
}

td, th {
  border: 1px solid #dddddd;
  text-align: left;
  padding: 8px;
}
</style></head>
<body>
<table>
<tr bgcolor='grey' ><th>Mount</th><th>Used%</th><th>Status</th></tr>
" > /tmp/test1.html
echo "@@{Output_var}@@" | sed 's/#~~#,//g' | sed 's/#~~#//g'>> /tmp/test1.html
echo "</table></body></html>" >> /tmp/test1.html

mail -s "Runbook Report @@{calm_now}@@" -a /tmp/test1.html @@{Mail_ID}@@  < /tmp/test2.txt
